import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.notificationSound = new Audio(process.env.PUBLIC_URL + "/notification-sound.mp3"); // Replace with your sound file

    this.handleNotificationClick = this.handleNotificationClick.bind(this);
  }

  handleNotificationClick() {
    if (!("Notification" in window)) {
      alert("This browser does not support desktop notification.");
    } else if (Notification.permission === "granted") {
      this.notificationSound.play(); // Play the notification sound
      const notification = new Notification("Hello from Push Notification!", {
        icon: process.env.PUBLIC_URL + "/logo192.png",
        body: "This is a sample push notification using React.",
      });

      notification.onclick = () => {
        console.log("Notification clicked!");
      };
    } else if (Notification.permission !== "denied") {
      Notification.requestPermission().then((permission) => {
        if (permission === "granted") {
          this.handleNotificationClick();
        }
      });
    }
  }

  render() {
    return (
      <div className="App">
        <h1>React Push Notifications</h1>
        <button onClick={this.handleNotificationClick}>Show Notification</button>
      </div>
    );
  }
}

export default App;
